#!/bin/bash\n# 🎯 Kaggle Content Automation - Setup Checker\n\necho \"🔧 Checking Kaggle Content Automation setup...\"\necho \"\"\n\n# Load config if exists\nif [[ -f \"config/config.env\" ]]; then\n    source config/config.env\n    echo \"✅ Loaded configuration\"\nelse\n    echo \"⚠️  Configuration not found (optional)\"\nfi\n\necho \"\"\n\n# Check Python/pip\necho \"1️⃣ Python Environment:\"\nif command -v python3 &> /dev/null; then\n    echo \"   ✅ Python3: $(python3 --version)\"\nelse\n    echo \"   ❌ Python3 not found\"\nfi\n\necho \"\"\n\n# Check Kaggle CLI\necho \"2️⃣ Kaggle CLI:\"\nif command -v kaggle &> /dev/null; then\n    echo \"   ✅ Kaggle CLI: $(kaggle --version)\"\n    \n    # Test API\n    if kaggle competitions list -p 1 &> /dev/null; then\n        echo \"   ✅ API authentication working\"\n    else\n        echo \"   ❌ API authentication failed\"\n        echo \"      👉 Check your kaggle.json configuration\"\n    fi\nelse\n    echo \"   ❌ Kaggle CLI not installed\"\n    echo \"      👉 Run: pipx install kaggle\"\nfi\n\necho \"\"\n\n# Check Git\necho \"3️⃣ Git:\"\nif command -v git &> /dev/null; then\n    echo \"   ✅ Git: $(git --version)\"\nelse\n    echo \"   ❌ Git not found\"\nfi\n\necho \"\"\n\n# Check scripts\necho \"4️⃣ Automation Scripts:\"\nfor script in \"mirror_to_kaggle.sh\" \"enhance_notebook.sh\" \"log_activity.sh\"; do\n    if [[ -x \"scripts/$script\" ]]; then\n        echo \"   ✅ $script\"\n    else\n        echo \"   ❌ $script (missing or not executable)\"\n    fi\ndone\n\necho \"\"\necho \"🎯 Setup Summary:\"\necho \"━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━\"\necho \"🚀 Ready to automate your Kaggle workflow!\"\necho \"\"\necho \"📚 Next steps:\"\necho \"   1. Configure: cp config/config.example.env config/config.env\"\necho \"   2. Test: ./scripts/mirror_to_kaggle.sh <github-repo>\"\necho \"   3. Enhance: ./scripts/enhance_notebook.sh <kaggle-notebook>\"